targets.query:
//SELECT sum("value") FROM "http_reqs" WHERE $timeFilter GROUP BY time(1s) fill(null)

from(bucket: v.bucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "http_reqs" and
    r._field == "value"
  )
  |> keep(columns: ["_time", "_value"])
  |> aggregateWindow(
    every: 1s,
    fn: sum,
    createEmpty: true
  )

