//SELECT mean("value") FROM "http_req_sending" WHERE $timeFilter and "group" =~/^$Transaction$/ and "name" =~/^$URL$/
//SELECT mean("value") FROM "http_req_waiting" WHERE $timeFilter and "group" =~/^$Transaction$/ and "name" =~/^$URL$/
//SELECT mean("value") FROM "http_req_receiving" WHERE $timeFilter and "group" =~/^$Transaction$/ and "name" =~/^$URL$/

from(bucket: v.bucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "http_req_duration" and
    r._field == "value"
  )
  |> filter(fn: (r) => contains(value: r.group, set: ${Transaction:json}))
  |> filter(fn: (r) => contains(value: r.name, set: ${URL:json}))
  |> group()
  |> histogram(
    column: "_value",
    upperBoundColumn: "le",
    countColumn: "count",
    bins: [3.0, 4.0, 10.0, 20.0, 40.0, 80.0, 160.0, 10000.0]
  )
  |> keep(columns: ["le", "count"])

